package pl.toby.comment;import pl.toby.core.misc.BaseEntity;import pl.toby.post.Post;import pl.toby.user.User;import javax.persistence.Entity;import javax.persistence.ManyToOne;import java.time.LocalDate;@Entitypublic class PostComment extends BaseEntity {    private String content;    private LocalDate date;    @ManyToOne    private User user;    @ManyToOne    private Post post;    public PostComment() {        super();    }    public PostComment(String content) {        this();        this.content = content;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    public LocalDate getDate() {        return date;    }    public void setDate(LocalDate date) {        this.date = date;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public Post getPost() {        return post;    }    public void setPost(Post post) {        this.post = post;    }}